sys_script_include.script:
var needitUtils = Class.create();
needitUtils.prototype = {
    initialize: function() {},
    isDatePast: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var rightnow = new GlideDateTime();
        var testdate = new GlideDateTime(strDate);
// 		gs.addInfoMessage("rightnow : " + rightnow);
// 		gs.addInfoMessage("testdate : " + testdate);


        // If the testdate is before rightnow, return true, else return false
        if (testdate.before(rightnow)) {
            return true;
        } else {
            return false;
        }
    },
	isDateToday : function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var rightnow = new GlideDateTime();
        var testdate = new GlideDateTime(strDate);
		var istoday = rightnow.getLocalDate();
		var date = testdate.getLocalDate();
		gs.addInfoMessage("rightnow : " + istoday);
		gs.addInfoMessage("testdate : " + date);

		gs.addInfoMessage(date.compareTo(istoday));


        // If the testdate is before rightnow, return true, else return false
        if (istoday != date) {
            return true;
        } else {
            return false;
        }
    },
    type: 'needitUtils'
};
